const basePath = 'https://ik.imagekit.io/baas';

const buildImageKitUrl = (width: number = 50, height: number = 50): string => {
    let transform = `tr:w-${width}`;
    return `${basePath}/${transform}/images/`;
}

const getProductPhotoPathFromImage = (item: any, width: number = 50, height: number = 50): string => {
    let i;
    if (item?.Image?.Filename) {
        i = item.Image;
        let parts = i.Filename.split("_");
        if (i.Origin == 'Upload') {
            return `${buildImageKitUrl(width, height)}upload/test/${parts[0]}.${item.Image.FileExtension}`;
        } else {
            return `${basePath}/tradelinq_commerce/${parts[0]}.${item.Image.FileExtension}`;
        }
    } else {
        i = item;
        let parts = i.Filename.split("_");
        if (i.Origin == 'Upload') {
            return `${buildImageKitUrl(width, height)}upload/test/${parts[0]}.${item.FileExtension}`;
        } else {
            return `${basePath}/tradelinq_commerce/${parts[0]}.${item.FileExtension}`;
        }
    }
    return "data:image/png;base64,";
}
export default function Image(props: any) {

    const { image, width, height } = props;

    return (
        <img src={getProductPhotoPathFromImage(image, width, height)} alt={image.Title} />
    )
}